import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.css";
import { library } from "@fortawesome/fontawesome-svg-core";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faGithub } from "@fortawesome/free-brands-svg-icons";
import { faLinkedin } from "@fortawesome/free-brands-svg-icons";

import Container from "react-bootstrap/Container";
import Nav from "react-bootstrap/Nav";
import Navbar from "react-bootstrap/Navbar";

library.add(faGithub, faLinkedin);

class NavBar extends Component {
  state = {
    pages: [
      { id: 0, classes: "nav-link" },
      { id: 1, classes: "nav-link" },
      { id: 2, classes: "nav-link" },
    ],
  };

  setActive = (linkId) => {
    const pages = this.state.pages.map((page) => {
      page.id === linkId
        ? (page.classes = "nav-link active")
        : (page.classes = "nav-link");
      return page;
    });
    this.setState({ pages });
  };

  getNavLinkClasses = (linkId) => {
    return this.state.pages.filter((page) => page.id === linkId)[0].classes;
  };

  render() {
    return (
      <Navbar expand="md" className="navbar-dark fixed-top bg-dark">
        <Container fluid>
          <Navbar.Brand href="#">MESSEY Ã  NDIOMO</Navbar.Brand>

          <div className="d-sm-flex">
            <Navbar.Toggle aria-controls="navbarScroll" />
            <Navbar.Collapse id="navbarScroll">
              <Nav
                className="me-auto my-2 my-lg-0"
                style={{ "--bs-scroll-height": 100 }}
                navbarScroll
              >
                <Nav.Link
                  href="#action1"
                  className={this.getNavLinkClasses(0)}
                  aria-current="page"
                  onClick={() => {
                    this.props.onPage(0);
                    this.setActive(0);
                  }}
                >
                  ABOUT
                </Nav.Link>
                <Nav.Link
                  href="#action2"
                  className={this.getNavLinkClasses(1)}
                  onClick={() => {
                    this.props.onPage(1);
                    this.setActive(1);
                  }}
                >
                  WORK
                </Nav.Link>
                <Nav.Link
                  href="#action3"
                  className={this.getNavLinkClasses(2)}
                  onClick={() => {
                    this.props.onPage(2);
                    this.setActive(2);
                  }}
                >
                  CONTACT
                </Nav.Link>
                <Nav.Link
                  className="nav-link"
                  href="https://github.com/messey-bilal"
                >
                  <FontAwesomeIcon icon={["fab", "fa-github"]} />
                </Nav.Link>
                <Nav.Link
                  className="nav-link"
                  href="https://www.linkedin.com/feed/?trk=guest_homepage-basic_google-one-tap-submit"
                >
                  <FontAwesomeIcon icon={["fab", "fa-linkedin"]} />
                </Nav.Link>
              </Nav>
            </Navbar.Collapse>
          </div>
        </Container>
      </Navbar>
    );
  }
}

export default NavBar;
