import React, { Component } from "react";
import { Carousel } from "react-bootstrap";

class Reception extends Component {
  state = {};

  getSrcImage = (num) => {
    let srcImage = "../imgs/slide-";
    if (window.innerWidth < 769) {
      srcImage += "sm";
    }
    srcImage += num + ".png";

    return require(srcImage);
  };

  render() {
    return (
      <Carousel>
        <Carousel.Item interval={2000}>
          <img
            src={this.getSrcImage(2)}
            className="img-fluid"
            alt=""
            width="100%"
            height="auto"
          />
        </Carousel.Item>
        <Carousel.Item interval={2000}>
          <img
            src={this.getSrcImage(3)}
            className="img-fluid"
            alt=""
            width="100%"
            height="auto"
          />
        </Carousel.Item>
        <Carousel.Item interval={2000}>
          <img
            src={this.getSrcImage(4)}
            className="img-fluid"
            alt=""
            width="100%"
            height="auto"
          />
        </Carousel.Item>
      </Carousel>
    );
  }
}

export default Reception;
