import "./App.css";
import React, { Component } from "react";
import NavBar from "./components/navbar";
import About from "./components/about";
import Work from "./components/work";

class App extends Component {
  state = {
    link: 0,
  };

  handleAbout = () => {
    this.setState({ link: 0 });
  };

  handleWork = () => {
    this.setState({ link: 1 });
  };

  handleContact = () => {
    this.setState({ link: 2 });
  };

  getClasses() {
    let classes = "container-fluid vh-100 d-flex align-items-center";
    if (this.state.link === 0) {
      classes += " bg-dark text-white";
    } else if (this.state.link === 1) {
      classes += " vw-100 justify-content-center";
    } else if (this.state.link === 2) {
    }
    return classes;
  }

  getPage() {
    let page = <About />;
    if (this.state.link === 0) {
      page = <About />;
    } else if (this.state.link === 1) {
      page = <Work />;
    } else if (this.state.link === 2) {
    }
    return page;
  }

  render() {
    return (
      <React.Fragment>
        <NavBar
          onAbout={this.handleAbout}
          onWork={this.handleWork}
          onContact={this.handleContact}
        />
        <main className={this.getClasses()}>{this.getPage()}</main>
      </React.Fragment>
    );
  }
}

export default App;
