import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.css";
import { library } from "@fortawesome/fontawesome-svg-core";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faGithub } from "@fortawesome/free-brands-svg-icons";
import { faLinkedin } from "@fortawesome/free-brands-svg-icons";

library.add(faGithub, faLinkedin);

class NavBar extends Component {
  state = {
    pages: [
      { id: 0, classes: "nav-link" },
      { id: 1, classes: "nav-link" },
      { id: 2, classes: "nav-link" },
    ],
  };

  setActive = (linkId) => {
    const pages = this.state.pages.map((page) => {
      page.id === linkId
        ? (page.classes = "nav-link active")
        : (page.classes = "nav-link");
      return page;
    });
    this.setState({ pages });
  };

  getNavLinkClasses = (linkId) => {
    return this.state.pages.filter((page) => page.id === linkId)[0].classes;
  };

  render() {
    return (
      <nav className="navbar navbar-expand-md navbar-dark fixed-top bg-dark">
        <div className="container-fluid">
          <a className="navbar-brand" href="#">
            MESSEY Ã  NDIOMO
          </a>
          <div className="d-flex">
            <button
              className="navbar-toggler"
              type="button"
              data-bs-toggle="collapse"
              data-bs-target="#navbarScroll"
              aria-controls="navbarScroll"
              aria-expanded="false"
              aria-label="Toggle navigation"
            >
              <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse navbar-collapse" id="navbarScroll">
              <ul
                className="navbar-nav me-auto my-2 my-lg-0 navbar-nav-scroll"
                style={{ "--bs-scroll-height": 100 }}
              >
                <li className="nav-item">
                  <a
                    className={this.getNavLinkClasses(0)}
                    aria-current="page"
                    href="#"
                    onClick={() => {
                      this.props.onPage(0);
                      this.setActive(0);
                    }}
                  >
                    ABOUT
                  </a>
                </li>
                <li className="nav-item">
                  <a
                    className={this.getNavLinkClasses(1)}
                    href="#"
                    onClick={() => {
                      this.props.onPage(1);
                      this.setActive(1);
                    }}
                  >
                    WORK
                  </a>
                </li>
                <li className="nav-item">
                  <a
                    className={this.getNavLinkClasses(2)}
                    href="#"
                    onClick={() => {
                      this.props.onPage(2);
                      this.setActive(2);
                    }}
                  >
                    CONTACT
                  </a>
                </li>
                <li className="nav-item">
                  <a
                    className="nav-link"
                    href="https://github.com/messey-bilal"
                  >
                    <FontAwesomeIcon icon={["fab", "fa-github"]} />
                  </a>
                </li>
                <li className="nav-item">
                  <a
                    className="nav-link"
                    href="https://www.linkedin.com/feed/?trk=guest_homepage-basic_google-one-tap-submit"
                  >
                    <FontAwesomeIcon icon={["fab", "fa-linkedin"]} />
                  </a>
                </li>
              </ul>
            </div>
          </div>
        </div>
      </nav>
    );
  }
}

export default NavBar;
